    def add_officer(self):
        officer_id = self.OfficerIDField.text()
        first_name = self.OfficerFnField.text()                                        
        last_name = self.OfficerLn.text() 
        dob = self.OfficerDOBField.text()                       
        gender = self.OfficerGenderField.text()
        badgenumber = self.OfficerBadgeField.text()
        rank = self.OfficerRankField.text()
        SupervisorID = self.OfficerSuperField.text()
        StationID = self.OfficerStationField.text()
        Email = self.OfficerEmailField.text()
        Phone = self.OfficerPhoneField.text()
        
        if officer_id == '' or first_name == '' or last_name == '' or gender == '' or dob == '' or rank == '' or badgenumber == '' or SupervisorID == '' or StationID == '' or Email == '' or Phone == '':
            QtWidgets.QMessageBox.warning(self, 'Error', 'Please enter all fields')
            return 
        try:
            officer_id = int(officer_id)
            SupervisorID=int(SupervisorID)
            StationID=int(StationID)
        except:
            QtWidgets.QMessageBox.warning(self, 'Error', 'Officer ID must be an integer')
            return
        tmp = Officer.get_all()
        tmp = [x[0] for x in tmp]
        if officer_id in tmp:
            QtWidgets.QMessageBox.warning(self, 'Error', 'Officer ID already exists')
            return
        if SupervisorID not in tmp:
            QtWidgets.QMessageBox.warning(self, 'Error', 'Supervisor ID must exists')
            return
        tmp=PoliceStation.get_all()
        tmp = [x[0] for x in tmp]
        if StationID not in tmp:
            QtWidgets.QMessageBox.warning(self, 'Error', 'Station ID must exists')
            return
        officer = Officer(officer_id, first_name, last_name,dob, gender,  badgenumber,rank, SupervisorID, StationID)
        officer_contact = OfficerContactInfo(officer_id, Email, Phone)
        officer_contact.insert_into_database()
        officer.insert_into_database()
        QtWidgets.QMessageBox.warning(self, 'Sucess', 'Officer Added Successfully')
        self.OfficerIDField.clear()
        self.OfficerFnField.clear()
        self.OfficerLn.clear()
        self.OfficerDOBField.clear()
        self.OfficerGenderField.clear()
        self.OfficerBadgeField.clear()
        self.OfficerRankField.clear()
        self.OfficerSuperField.clear()
        self.OfficerStationField.clear()

